gameActivityReadMetadataAdditions.insert:

if (bundle.containsKey("com.epicgames.ue4.ChartBoost.AppId") && bundle.containsKey("com.epicgames.ue4.ChartBoost.AppSignature")) {
	String appId = bundle.getString("com.epicgames.ue4.ChartBoost.AppId");
	String appSignature = bundle.getString("com.epicgames.ue4.ChartBoost.AppSignature");

	Log.debug("Chartboost.startWithAppId(\"" + appId + "\", \"" + appSignature + "\")");

	Chartboost.startWithAppId(this, appId, appSignature);
	Chartboost.setDelegate(cbDelegate);
	Chartboost.onCreate(this);
} else {
	Log.debug("Missing Chartboost App ID and/or App Signature on \"Extra settings for <application>\" in your project's Android settings. Example:\n <meta-data android:name=\"com.epicgames.ue4.ChartBoost.AppId\" android:value=\"Your App Id\" />\\n<meta-data android:name=\"com.epicgames.ue4.ChartBoost.AppSignature\" android:value=\"Your App Signature\" />");
}
